SELECT 
	quote_id, quote_price, quote_qty, quote_currency, quote_spec_1, quote_spec_2, quote_spec_3, quote_spec_4, quote_spec_5, quote_spec_6, quote_spec_7, quote_spec_8, quote_spec_9, quote_spec_10, remark1, remark2, remark3, remark4, remark5, crt_user, crt_host, crt_time, upd_user, upd_host, upd_time, crt_user_no, crt_user_name, upd_user_no, upd_user_name
FROM cq_quote_b;


SELECT
	quote_id, quote_price, quote_qty, quote_currency, quote_spec_1, quote_spec_2, quote_spec_3, quote_spec_4, quote_spec_5, quote_spec_6, quote_spec_7, quote_spec_8, quote_spec_9, quote_spec_10, remark1, remark2, remark3, remark4, remark5, crt_user, crt_host, crt_time, upd_user, upd_host, upd_time, crt_user_no, crt_user_name, upd_user_no, upd_user_name
FROM cq_quote_b;


INSERT INTO cq_quote_b 
(quote_id, quote_price, quote_qty, quote_currency, quote_spec_1, quote_spec_2, quote_spec_3, quote_spec_4, quote_spec_5, quote_spec_6, quote_spec_7, quote_spec_8, quote_spec_9, quote_spec_10, remark1, remark2, remark3, remark4, remark5, crt_user, crt_host, crt_time, upd_user, upd_host, upd_time, crt_user_no, crt_user_name, upd_user_no, upd_user_name)
VALUES (@quote_id, @quote_price, @quote_qty, @quote_currency, @quote_spec_1, @quote_spec_2, @quote_spec_3, @quote_spec_4, @quote_spec_5, @quote_spec_6, @quote_spec_7, @quote_spec_8, @quote_spec_9, @quote_spec_10, @remark1, @remark2, @remark3, @remark4, @remark5, @crt_user, @crt_host, @crt_time, @upd_user, @upd_host, @upd_time, @crt_user_no, @crt_user_name, @upd_user_no, @upd_user_name);
