SELECT 
	cr_dlv_h_no, cr_dlv_h_status, cr_dlv_h_status_name, cr_dlv_datetime, client_no, client_name, so_h_no, cr_dlv_h_amount, cr_dlv_h_currency, cr_dlv_h_amount_due_date, cr_dlv_h_rmk1, cr_dlv_h_rmk2, cr_dlv_h_rmk3, cr_dlv_h_rmk4, cr_dlv_h_rmk5, cr_dlv_h_rmk6, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host
FROM cr_dlv_h;


INSERT INTO cr_dlv_h 
(cr_dlv_h_no, cr_dlv_h_status, cr_dlv_h_status_name, cr_dlv_datetime, client_no, client_name, so_h_no, cr_dlv_h_amount, cr_dlv_h_currency, cr_dlv_h_amount_due_date, cr_dlv_h_rmk1, cr_dlv_h_rmk2, cr_dlv_h_rmk3, cr_dlv_h_rmk4, cr_dlv_h_rmk5, cr_dlv_h_rmk6, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host)
VALUES (@cr_dlv_h_no, @cr_dlv_h_status, @cr_dlv_h_status_name, @cr_dlv_datetime, @client_no, @client_name, @so_h_no, @cr_dlv_h_amount, @cr_dlv_h_currency, @cr_dlv_h_amount_due_date, @cr_dlv_h_rmk1, @cr_dlv_h_rmk2, @cr_dlv_h_rmk3, @cr_dlv_h_rmk4, @cr_dlv_h_rmk5, @cr_dlv_h_rmk6, @crt_time, @crt_user, @crt_user_no, @crt_user_name, @crt_host, @upd_time, @upd_user, @upd_user_no, @upd_user_name, @upd_host);


UPDATE cr_dlv_h SET 
cr_dlv_h_status = @cr_dlv_h_status, cr_dlv_h_status_name = @cr_dlv_h_status_name, cr_dlv_datetime = @cr_dlv_datetime, client_no = @client_no, client_name = @client_name, so_h_no = @so_h_no, cr_dlv_h_amount = @cr_dlv_h_amount, cr_dlv_h_currency = @cr_dlv_h_currency, cr_dlv_h_amount_due_date = @cr_dlv_h_amount_due_date, cr_dlv_h_rmk1 = @cr_dlv_h_rmk1, cr_dlv_h_rmk2 = @cr_dlv_h_rmk2, cr_dlv_h_rmk3 = @cr_dlv_h_rmk3, cr_dlv_h_rmk4 = @cr_dlv_h_rmk4, cr_dlv_h_rmk5 = @cr_dlv_h_rmk5, cr_dlv_h_rmk6 = @cr_dlv_h_rmk6, crt_time = @crt_time, crt_user = @crt_user, crt_user_no = @crt_user_no, crt_user_name = @crt_user_name, crt_host = @crt_host, upd_time = @upd_time, upd_user = @upd_user, upd_user_no = @upd_user_no, upd_user_name = @upd_user_name, upd_host = @upd_host
WHERE cr_dlv_h_no = @cr_dlv_h_no OR cr_dlv_h_id = @cr_dlv_h_id;
