SELECT 
	cr_dlv_his_id, client_no, part_no, part_idt, cr_dlv_his_year, cr_dlv_his_week, cr_dlv_his_qty, mrp_region_no, cr_dlv_his_rmk1, cr_dlv_his_rmk2, cr_dlv_his_rmk3, cr_dlv_his_rmk4, cr_dlv_his_rmk5, cr_dlv_his_rmk6, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host
FROM cr_dlv_his;


SELECT
	cr_dlv_his_id, client_no, part_no, part_idt, cr_dlv_his_year, cr_dlv_his_week, cr_dlv_his_qty, mrp_region_no, cr_dlv_his_rmk1, cr_dlv_his_rmk2, cr_dlv_his_rmk3, cr_dlv_his_rmk4, cr_dlv_his_rmk5, cr_dlv_his_rmk6, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host
FROM cr_dlv_his;


INSERT INTO cr_dlv_his 
(cr_dlv_his_id, client_no, part_no, part_idt, cr_dlv_his_year, cr_dlv_his_week, cr_dlv_his_qty, mrp_region_no, cr_dlv_his_rmk1, cr_dlv_his_rmk2, cr_dlv_his_rmk3, cr_dlv_his_rmk4, cr_dlv_his_rmk5, cr_dlv_his_rmk6, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host)
VALUES (@cr_dlv_his_id, @client_no, @part_no, @part_idt, @cr_dlv_his_year, @cr_dlv_his_week, @cr_dlv_his_qty, @mrp_region_no, @cr_dlv_his_rmk1, @cr_dlv_his_rmk2, @cr_dlv_his_rmk3, @cr_dlv_his_rmk4, @cr_dlv_his_rmk5, @cr_dlv_his_rmk6, @crt_time, @crt_user, @crt_user_no, @crt_user_name, @crt_host, @upd_time, @upd_user, @upd_user_no, @upd_user_name, @upd_host);
