sqls.sql:

SELECT 
	cr_rtn_h_id, sn_no, part_no, part_name, part_spec, part_unit, part_unit_name, part_idt, mrp_region_no, lot_no, invp_no, invp_name, part_qty, cr_rtn_sn_part_rmk1, cr_rtn_sn_part_rmk2, cr_rtn_sn_part_rmk3, cr_rtn_sn_part_rmk4, cr_rtn_sn_part_rmk5, cr_rtn_sn_part_rmk6, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host
FROM cr_rtn_sn_part;


INSERT INTO cr_rtn_sn_part 
(cr_rtn_h_id, sn_no, part_no, part_name, part_spec, part_unit, part_unit_name, part_idt, mrp_region_no, lot_no, invp_no, invp_name, part_qty, cr_rtn_sn_part_rmk1, cr_rtn_sn_part_rmk2, cr_rtn_sn_part_rmk3, cr_rtn_sn_part_rmk4, cr_rtn_sn_part_rmk5, cr_rtn_sn_part_rmk6, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host)
VALUES (@cr_rtn_h_id, @sn_no, @part_no, @part_name, @part_spec, @part_unit, @part_unit_name, @part_idt, @mrp_region_no, @lot_no, @invp_no, @invp_name, @part_qty, @cr_rtn_sn_part_rmk1, @cr_rtn_sn_part_rmk2, @cr_rtn_sn_part_rmk3, @cr_rtn_sn_part_rmk4, @cr_rtn_sn_part_rmk5, @cr_rtn_sn_part_rmk6, @crt_time, @crt_user, @crt_user_no, @crt_user_name, @crt_host, @upd_time, @upd_user, @upd_user_no, @upd_user_name, @upd_host);


UPDATE cr_rtn_sn_part SET 
cr_rtn_h_id = @cr_rtn_h_id, sn_no = @sn_no, part_no = @part_no, part_name = @part_name, part_spec = @part_spec, part_unit = @part_unit, part_unit_name = @part_unit_name, part_idt = @part_idt, mrp_region_no = @mrp_region_no, lot_no = @lot_no, invp_no = @invp_no, invp_name = @invp_name, part_qty = @part_qty, cr_rtn_sn_part_rmk1 = @cr_rtn_sn_part_rmk1, cr_rtn_sn_part_rmk2 = @cr_rtn_sn_part_rmk2, cr_rtn_sn_part_rmk3 = @cr_rtn_sn_part_rmk3, cr_rtn_sn_part_rmk4 = @cr_rtn_sn_part_rmk4, cr_rtn_sn_part_rmk5 = @cr_rtn_sn_part_rmk5, cr_rtn_sn_part_rmk6 = @cr_rtn_sn_part_rmk6, crt_time = @crt_time, crt_user = @crt_user, crt_user_no = @crt_user_no, crt_user_name = @crt_user_name, crt_host = @crt_host, upd_time = @upd_time, upd_user = @upd_user, upd_user_no = @upd_user_no, upd_user_name = @upd_user_name, upd_host = @upd_host
WHERE cr_rtn_sn_part_id = @cr_rtn_sn_part_id;
