sqls.sql:

SELECT 
	cr_rtn_h_id, sn_no, sn_type, sn_type_name, invp_no, invp_name, cr_rtn_sn_rmk1, cr_rtn_sn_rmk2, cr_rtn_sn_rmk3, cr_rtn_sn_rmk4, cr_rtn_sn_rmk5, cr_rtn_sn_rmk6, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host
FROM cr_rtn_sn;


INSERT INTO cr_rtn_sn 
(cr_rtn_h_id, sn_no, sn_type, sn_type_name, invp_no, invp_name, cr_rtn_sn_rmk1, cr_rtn_sn_rmk2, cr_rtn_sn_rmk3, cr_rtn_sn_rmk4, cr_rtn_sn_rmk5, cr_rtn_sn_rmk6, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host)
VALUES (@cr_rtn_h_id, @sn_no, @sn_type, @sn_type_name, @invp_no, @invp_name, @cr_rtn_sn_rmk1, @cr_rtn_sn_rmk2, @cr_rtn_sn_rmk3, @cr_rtn_sn_rmk4, @cr_rtn_sn_rmk5, @cr_rtn_sn_rmk6, @crt_time, @crt_user, @crt_user_no, @crt_user_name, @crt_host, @upd_time, @upd_user, @upd_user_no, @upd_user_name, @upd_host);


UPDATE cr_rtn_sn SET 
cr_rtn_h_id = @cr_rtn_h_id, sn_no = @sn_no, sn_type = @sn_type, sn_type_name = @sn_type_name, invp_no = @invp_no, invp_name = @invp_name, cr_rtn_sn_rmk1 = @cr_rtn_sn_rmk1, cr_rtn_sn_rmk2 = @cr_rtn_sn_rmk2, cr_rtn_sn_rmk3 = @cr_rtn_sn_rmk3, cr_rtn_sn_rmk4 = @cr_rtn_sn_rmk4, cr_rtn_sn_rmk5 = @cr_rtn_sn_rmk5, cr_rtn_sn_rmk6 = @cr_rtn_sn_rmk6, crt_time = @crt_time, crt_user = @crt_user, crt_user_no = @crt_user_no, crt_user_name = @crt_user_name, crt_host = @crt_host, upd_time = @upd_time, upd_user = @upd_user, upd_user_no = @upd_user_no, upd_user_name = @upd_user_name, upd_host = @upd_host
WHERE cr_rtn_sn_id = @cr_rtn_sn_id;
