SELECT 
	demo_b_id, demo_h_id, vachar1, vachar2, vachar3, vachar4, vachar5, vachar6, vachar7, vachar8, int1, int2, int3, numeric1, numeric2, numeric3, bool1, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host
FROM demo_c;


SELECT
	demo_b_id, demo_h_id, vachar1, vachar2, vachar3, vachar4, vachar5, vachar6, vachar7, vachar8, int1, int2, int3, numeric1, numeric2, numeric3, bool1, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host
FROM demo_c;


INSERT INTO demo_c 
(demo_b_id, demo_h_id, vachar1, vachar2, vachar3, vachar4, vachar5, vachar6, vachar7, vachar8, int1, int2, int3, numeric1, numeric2, numeric3, bool1, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host)
VALUES (@demo_b_id, @demo_h_id, @vachar1, @vachar2, @vachar3, @vachar4, @vachar5, @vachar6, @vachar7, @vachar8, @int1, @int2, @int3, @numeric1, @numeric2, @numeric3, @bool1, @crt_time, @crt_user, @crt_user_no, @crt_user_name, @crt_host, @upd_time, @upd_user, @upd_user_no, @upd_user_name, @upd_host);
