sqls.sql:

SELECT 
	file_release_id, file_release, file_size, file_store_path, file_thumb_store_path, file_is_compressed, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host
FROM dm_file_release;


INSERT INTO dm_file_release 
(file_release_id, file_release, file_size, file_store_path, file_thumb_store_path, file_is_compressed, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host)
VALUES (@file_release_id, @file_release, @file_size, @file_store_path, @file_thumb_store_path, @file_is_compressed, @crt_time, @crt_user, @crt_user_no, @crt_user_name, @crt_host, @upd_time, @upd_user, @upd_user_no, @upd_user_name, @upd_host);


UPDATE dm_file_release SET 
file_release_id = @file_release_id, file_release = @file_release, file_size = @file_size, file_store_path = @file_store_path, file_thumb_store_path = @file_thumb_store_path, file_is_compressed = @file_is_compressed, crt_time = @crt_time, crt_user = @crt_user, crt_user_no = @crt_user_no, crt_user_name = @crt_user_name, crt_host = @crt_host, upd_time = @upd_time, upd_user = @upd_user, upd_user_no = @upd_user_no, upd_user_name = @upd_user_name, upd_host = @upd_host
WHERE file_id = @file_id;
