SELECT 
	ea_check_item_h_id, ea_check_item_seq, ea_check_item_name, ea_check_type, ea_check_value_std, ea_check_value_min, ea_check_value_max, ea_check_cycle_day, ea_check_item_b_rmk1, ea_check_item_b_rmk2, ea_check_item_b_rmk3, ea_check_item_b_rmk4, ea_check_item_b_rmk5, ea_check_item_b_rmk6, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host
FROM ea_check_item_b;


INSERT INTO ea_check_item_b 
(ea_check_item_h_id, ea_check_item_seq, ea_check_item_name, ea_check_type, ea_check_value_std, ea_check_value_min, ea_check_value_max, ea_check_cycle_day, ea_check_item_b_rmk1, ea_check_item_b_rmk2, ea_check_item_b_rmk3, ea_check_item_b_rmk4, ea_check_item_b_rmk5, ea_check_item_b_rmk6, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host)
VALUES (@ea_check_item_h_id, @ea_check_item_seq, @ea_check_item_name, @ea_check_type, @ea_check_value_std, @ea_check_value_min, @ea_check_value_max, @ea_check_cycle_day, @ea_check_item_b_rmk1, @ea_check_item_b_rmk2, @ea_check_item_b_rmk3, @ea_check_item_b_rmk4, @ea_check_item_b_rmk5, @ea_check_item_b_rmk6, @crt_time, @crt_user, @crt_user_no, @crt_user_name, @crt_host, @upd_time, @upd_user, @upd_user_no, @upd_user_name, @upd_host);


UPDATE ea_check_item_b SET 
ea_check_item_h_id = @ea_check_item_h_id, ea_check_item_seq = @ea_check_item_seq, ea_check_item_name = @ea_check_item_name, ea_check_type = @ea_check_type, ea_check_value_std = @ea_check_value_std, ea_check_value_min = @ea_check_value_min, ea_check_value_max = @ea_check_value_max, ea_check_cycle_day = @ea_check_cycle_day, ea_check_item_b_rmk1 = @ea_check_item_b_rmk1, ea_check_item_b_rmk2 = @ea_check_item_b_rmk2, ea_check_item_b_rmk3 = @ea_check_item_b_rmk3, ea_check_item_b_rmk4 = @ea_check_item_b_rmk4, ea_check_item_b_rmk5 = @ea_check_item_b_rmk5, ea_check_item_b_rmk6 = @ea_check_item_b_rmk6, crt_time = @crt_time, crt_user = @crt_user, crt_user_no = @crt_user_no, crt_user_name = @crt_user_name, crt_host = @crt_host, upd_time = @upd_time, upd_user = @upd_user, upd_user_no = @upd_user_no, upd_user_name = @upd_user_name, upd_host = @upd_host
WHERE ea_check_item_b_id = @ea_check_item_b_id;
