sqls.sql:

SELECT 
	ea_no, ea_check_item_m_rmk1, ea_check_item_m_rmk2, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host
FROM ea_check_item_m;


INSERT INTO ea_check_item_m 
(ea_no, ea_check_item_m_rmk1, ea_check_item_m_rmk2, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host)
VALUES (@ea_no, @ea_check_item_m_rmk1, @ea_check_item_m_rmk2, @crt_time, @crt_user, @crt_user_no, @crt_user_name, @crt_host, @upd_time, @upd_user, @upd_user_no, @upd_user_name, @upd_host);


UPDATE ea_check_item_m SET 
ea_no = @ea_no, ea_check_item_m_rmk1 = @ea_check_item_m_rmk1, ea_check_item_m_rmk2 = @ea_check_item_m_rmk2, crt_time = @crt_time, crt_user = @crt_user, crt_user_no = @crt_user_no, crt_user_name = @crt_user_name, crt_host = @crt_host, upd_time = @upd_time, upd_user = @upd_user, upd_user_no = @upd_user_no, upd_user_name = @upd_user_name, upd_host = @upd_host
WHERE ea_check_item_h_id = @ea_check_item_h_id;
