sqls.sql:

SELECT 
	da_act_id, worker_id, da_act_worker_rmk01, da_act_worker_rmk02, da_act_worker_rmk03, da_act_worker_rmk04, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user_no, upd_user_name, upd_user, upd_host
FROM me_da_act_worker;


INSERT INTO me_da_act_worker 
(da_act_id, worker_id, da_act_worker_rmk01, da_act_worker_rmk02, da_act_worker_rmk03, da_act_worker_rmk04, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user_no, upd_user_name, upd_user, upd_host)
VALUES (@da_act_id, @worker_id, @da_act_worker_rmk01, @da_act_worker_rmk02, @da_act_worker_rmk03, @da_act_worker_rmk04, @crt_time, @crt_user, @crt_user_no, @crt_user_name, @crt_host, @upd_time, @upd_user_no, @upd_user_name, @upd_user, @upd_host);


UPDATE me_da_act_worker SET 
worker_id = @worker_id, da_act_worker_rmk01 = @da_act_worker_rmk01, da_act_worker_rmk02 = @da_act_worker_rmk02, da_act_worker_rmk03 = @da_act_worker_rmk03, da_act_worker_rmk04 = @da_act_worker_rmk04, crt_time = @crt_time, crt_user = @crt_user, crt_user_no = @crt_user_no, crt_user_name = @crt_user_name, crt_host = @crt_host, upd_time = @upd_time, upd_user_no = @upd_user_no, upd_user_name = @upd_user_name, upd_user = @upd_user, upd_host = @upd_host
WHERE da_act_id = @da_act_id OR rid = @rid;
