SELECT 
	is_exist, me_sn_no_rmk01, me_sn_no_rmk02, me_sn_no_rmk03, me_sn_no_rmk04, me_sn_no_rmk05, me_sn_no_rmk06, me_sn_no_rmk07, me_sn_no_rmk08, me_sn_no_rmk09, me_sn_no_rmk10, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host
FROM me_sn_no;


INSERT INTO me_sn_no 
(is_exist, me_sn_no_rmk01, me_sn_no_rmk02, me_sn_no_rmk03, me_sn_no_rmk04, me_sn_no_rmk05, me_sn_no_rmk06, me_sn_no_rmk07, me_sn_no_rmk08, me_sn_no_rmk09, me_sn_no_rmk10, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host)
VALUES (@is_exist, @me_sn_no_rmk01, @me_sn_no_rmk02, @me_sn_no_rmk03, @me_sn_no_rmk04, @me_sn_no_rmk05, @me_sn_no_rmk06, @me_sn_no_rmk07, @me_sn_no_rmk08, @me_sn_no_rmk09, @me_sn_no_rmk10, @crt_time, @crt_user, @crt_user_no, @crt_user_name, @crt_host, @upd_time, @upd_user, @upd_user_no, @upd_user_name, @upd_host);


UPDATE me_sn_no SET 
is_exist = @is_exist, me_sn_no_rmk01 = @me_sn_no_rmk01, me_sn_no_rmk02 = @me_sn_no_rmk02, me_sn_no_rmk03 = @me_sn_no_rmk03, me_sn_no_rmk04 = @me_sn_no_rmk04, me_sn_no_rmk05 = @me_sn_no_rmk05, me_sn_no_rmk06 = @me_sn_no_rmk06, me_sn_no_rmk07 = @me_sn_no_rmk07, me_sn_no_rmk08 = @me_sn_no_rmk08, me_sn_no_rmk09 = @me_sn_no_rmk09, me_sn_no_rmk10 = @me_sn_no_rmk10, crt_time = @crt_time, crt_user = @crt_user, crt_user_no = @crt_user_no, crt_user_name = @crt_user_name, crt_host = @crt_host, upd_time = @upd_time, upd_user = @upd_user, upd_user_no = @upd_user_no, upd_user_name = @upd_user_name, upd_host = @upd_host
WHERE me_sn_no = @me_sn_no OR me_sn_no_id = @me_sn_no_id;
