SELECT 
	wkc_priority, wkc_no, wkc_is_enabled, wkc_is_default, wkt_before_c, wkt_after_c, wkt_produce_c, qty_produce_c, wkt_worker_c, wkc_worker_num_c, wkt_before_b, wkt_after_b, wkt_produce_b, qty_produce_b, wkt_worker_b, wkc_worker_num_b, wkt_before_a, wkt_after_a, wkt_produce_a, qty_produce_a, wkt_worker_a, wkc_worker_num_a, mo_rmb_wkc_rmk01, mo_rmb_wkc_rmk02, mo_rmb_wkc_rmk03, mo_rmb_wkc_rmk04, mo_rmb_wkc_rmk05, mo_rmb_wkc_rmk06, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user_no, upd_user, upd_user_name, upd_host
FROM mo_rmb_wkc;


INSERT INTO mo_rmb_wkc 
(wkc_priority, wkc_no, wkc_is_enabled, wkc_is_default, wkt_before_c, wkt_after_c, wkt_produce_c, qty_produce_c, wkt_worker_c, wkc_worker_num_c, wkt_before_b, wkt_after_b, wkt_produce_b, qty_produce_b, wkt_worker_b, wkc_worker_num_b, wkt_before_a, wkt_after_a, wkt_produce_a, qty_produce_a, wkt_worker_a, wkc_worker_num_a, mo_rmb_wkc_rmk01, mo_rmb_wkc_rmk02, mo_rmb_wkc_rmk03, mo_rmb_wkc_rmk04, mo_rmb_wkc_rmk05, mo_rmb_wkc_rmk06, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user_no, upd_user, upd_user_name, upd_host)
VALUES (@wkc_priority, @wkc_no, @wkc_is_enabled, @wkc_is_default, @wkt_before_c, @wkt_after_c, @wkt_produce_c, @qty_produce_c, @wkt_worker_c, @wkc_worker_num_c, @wkt_before_b, @wkt_after_b, @wkt_produce_b, @qty_produce_b, @wkt_worker_b, @wkc_worker_num_b, @wkt_before_a, @wkt_after_a, @wkt_produce_a, @qty_produce_a, @wkt_worker_a, @wkc_worker_num_a, @mo_rmb_wkc_rmk01, @mo_rmb_wkc_rmk02, @mo_rmb_wkc_rmk03, @mo_rmb_wkc_rmk04, @mo_rmb_wkc_rmk05, @mo_rmb_wkc_rmk06, @crt_time, @crt_user, @crt_user_no, @crt_user_name, @crt_host, @upd_time, @upd_user_no, @upd_user, @upd_user_name, @upd_host);


UPDATE mo_rmb_wkc SET 
wkc_priority = @wkc_priority, wkc_no = @wkc_no, wkc_is_enabled = @wkc_is_enabled, wkc_is_default = @wkc_is_default, wkt_before_c = @wkt_before_c, wkt_after_c = @wkt_after_c, wkt_produce_c = @wkt_produce_c, qty_produce_c = @qty_produce_c, wkt_worker_c = @wkt_worker_c, wkc_worker_num_c = @wkc_worker_num_c, wkt_before_b = @wkt_before_b, wkt_after_b = @wkt_after_b, wkt_produce_b = @wkt_produce_b, qty_produce_b = @qty_produce_b, wkt_worker_b = @wkt_worker_b, wkc_worker_num_b = @wkc_worker_num_b, wkt_before_a = @wkt_before_a, wkt_after_a = @wkt_after_a, wkt_produce_a = @wkt_produce_a, qty_produce_a = @qty_produce_a, wkt_worker_a = @wkt_worker_a, wkc_worker_num_a = @wkc_worker_num_a, mo_rmb_wkc_rmk01 = @mo_rmb_wkc_rmk01, mo_rmb_wkc_rmk02 = @mo_rmb_wkc_rmk02, mo_rmb_wkc_rmk03 = @mo_rmb_wkc_rmk03, mo_rmb_wkc_rmk04 = @mo_rmb_wkc_rmk04, mo_rmb_wkc_rmk05 = @mo_rmb_wkc_rmk05, mo_rmb_wkc_rmk06 = @mo_rmb_wkc_rmk06, crt_time = @crt_time, crt_user = @crt_user, crt_user_no = @crt_user_no, crt_user_name = @crt_user_name, crt_host = @crt_host, upd_time = @upd_time, upd_user_no = @upd_user_no, upd_user = @upd_user, upd_user_name = @upd_user_name, upd_host = @upd_host
WHERE unode = @unode;
