SELECT
	rmh_id, rmh_status, rmh_no, part_no, part_idt, rmh_version, rmh_revision, is_default, rmh_desc, factory_no, rmh_type1, rmh_type2, pd_rmh_rmk1, pd_rmh_rmk2, pd_rmh_rmk3, pd_rmh_rmk4, pd_rmh_rmk5, pd_rmh_rmk6, rmh_wkn_num, rmh_wkt_produce, rmh_unit_cost, rmh_mtr_weight, print_times, is_plan, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host
FROM pd_rmh_import_tmp;


INSERT INTO pd_rmh_import_tmp 
(rmh_id, rmh_status, rmh_no, part_no, part_idt, rmh_version, rmh_revision, is_default, rmh_desc, factory_no, rmh_type1, rmh_type2, pd_rmh_rmk1, pd_rmh_rmk2, pd_rmh_rmk3, pd_rmh_rmk4, pd_rmh_rmk5, pd_rmh_rmk6, rmh_wkn_num, rmh_wkt_produce, rmh_unit_cost, rmh_mtr_weight, print_times, is_plan, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host)
VALUES (@rmh_id, @rmh_status, @rmh_no, @part_no, @part_idt, @rmh_version, @rmh_revision, @is_default, @rmh_desc, @factory_no, @rmh_type1, @rmh_type2, @pd_rmh_rmk1, @pd_rmh_rmk2, @pd_rmh_rmk3, @pd_rmh_rmk4, @pd_rmh_rmk5, @pd_rmh_rmk6, @rmh_wkn_num, @rmh_wkt_produce, @rmh_unit_cost, @rmh_mtr_weight, @print_times, @is_plan, @crt_time, @crt_user, @crt_user_no, @crt_user_name, @crt_host, @upd_time, @upd_user, @upd_user_no, @upd_user_name, @upd_host);
