SELECT 
	pd_wkc_wka_id, wkc_id, wka_no, wka_name, wka_desc, pd_wkc_wka_rmk1, pd_wkc_wka_rmk2, pd_wkc_wka_rmk3, pd_wkc_wka_rmk4, pd_wkc_wka_rmk5, pd_wkc_wka_rmk6, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host
FROM pd_wkc_wka_import_tmp;


SELECT
	pd_wkc_wka_id, wkc_id, wka_no, wka_name, wka_desc, pd_wkc_wka_rmk1, pd_wkc_wka_rmk2, pd_wkc_wka_rmk3, pd_wkc_wka_rmk4, pd_wkc_wka_rmk5, pd_wkc_wka_rmk6, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host
FROM pd_wkc_wka_import_tmp;


INSERT INTO pd_wkc_wka_import_tmp 
(pd_wkc_wka_id, wkc_id, wka_no, wka_name, wka_desc, pd_wkc_wka_rmk1, pd_wkc_wka_rmk2, pd_wkc_wka_rmk3, pd_wkc_wka_rmk4, pd_wkc_wka_rmk5, pd_wkc_wka_rmk6, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host)
VALUES (@pd_wkc_wka_id, @wkc_id, @wka_no, @wka_name, @wka_desc, @pd_wkc_wka_rmk1, @pd_wkc_wka_rmk2, @pd_wkc_wka_rmk3, @pd_wkc_wka_rmk4, @pd_wkc_wka_rmk5, @pd_wkc_wka_rmk6, @crt_time, @crt_user, @crt_user_no, @crt_user_name, @crt_host, @upd_time, @upd_user, @upd_user_no, @upd_user_name, @upd_host);
