sqls.sql:

SELECT 
	pd_wksbom_hid, wkc_no, pd_wksbom_m_rmk01, pd_wksbom_m_rmk02, pd_wksbom_m_rmk03, pd_wksbom_m_rmk04, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host
FROM pd_wksbom_m;


INSERT INTO pd_wksbom_m 
(pd_wksbom_hid, wkc_no, pd_wksbom_m_rmk01, pd_wksbom_m_rmk02, pd_wksbom_m_rmk03, pd_wksbom_m_rmk04, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host)
VALUES (@pd_wksbom_hid, @wkc_no, @pd_wksbom_m_rmk01, @pd_wksbom_m_rmk02, @pd_wksbom_m_rmk03, @pd_wksbom_m_rmk04, @crt_time, @crt_user, @crt_user_no, @crt_user_name, @crt_host, @upd_time, @upd_user, @upd_user_no, @upd_user_name, @upd_host);


UPDATE pd_wksbom_m SET 
pd_wksbom_hid = @pd_wksbom_hid, wkc_no = @wkc_no, pd_wksbom_m_rmk01 = @pd_wksbom_m_rmk01, pd_wksbom_m_rmk02 = @pd_wksbom_m_rmk02, pd_wksbom_m_rmk03 = @pd_wksbom_m_rmk03, pd_wksbom_m_rmk04 = @pd_wksbom_m_rmk04, crt_time = @crt_time, crt_user = @crt_user, crt_user_no = @crt_user_no, crt_user_name = @crt_user_name, crt_host = @crt_host, upd_time = @upd_time, upd_user = @upd_user, upd_user_no = @upd_user_no, upd_user_name = @upd_user_name, upd_host = @upd_host
WHERE pd_wksbom_mid = @pd_wksbom_mid;
