SELECT 
	sr_part_map_id, part_no, supplier_no, supplier_part_no, supplier_part_name, supplier_part_spec, supplier_part_brand, assign_proportion, purchase_qty_min, purchase_qty_add, purchase_lead_time, unit_price, price_currency, si_type, si_degree, sr_part_map_rmk01, sr_part_map_rmk02, sr_part_map_rmk03, sr_part_map_rmk04, sr_part_map_rmk05, sr_part_map_rmk06, sr_part_map_rmk07, sr_part_map_rmk08, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host
FROM sr_part_map_import_tmp;


SELECT
	sr_part_map_id, part_no, supplier_no, supplier_part_no, supplier_part_name, supplier_part_spec, supplier_part_brand, assign_proportion, purchase_qty_min, purchase_qty_add, purchase_lead_time, unit_price, price_currency, si_type, si_degree, sr_part_map_rmk01, sr_part_map_rmk02, sr_part_map_rmk03, sr_part_map_rmk04, sr_part_map_rmk05, sr_part_map_rmk06, sr_part_map_rmk07, sr_part_map_rmk08, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host
FROM sr_part_map_import_tmp;


INSERT INTO sr_part_map_import_tmp 
(sr_part_map_id, part_no, supplier_no, supplier_part_no, supplier_part_name, supplier_part_spec, supplier_part_brand, assign_proportion, purchase_qty_min, purchase_qty_add, purchase_lead_time, unit_price, price_currency, si_type, si_degree, sr_part_map_rmk01, sr_part_map_rmk02, sr_part_map_rmk03, sr_part_map_rmk04, sr_part_map_rmk05, sr_part_map_rmk06, sr_part_map_rmk07, sr_part_map_rmk08, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host)
VALUES (@sr_part_map_id, @part_no, @supplier_no, @supplier_part_no, @supplier_part_name, @supplier_part_spec, @supplier_part_brand, @assign_proportion, @purchase_qty_min, @purchase_qty_add, @purchase_lead_time, @unit_price, @price_currency, @si_type, @si_degree, @sr_part_map_rmk01, @sr_part_map_rmk02, @sr_part_map_rmk03, @sr_part_map_rmk04, @sr_part_map_rmk05, @sr_part_map_rmk06, @sr_part_map_rmk07, @sr_part_map_rmk08, @crt_time, @crt_user, @crt_user_no, @crt_user_name, @crt_host, @upd_time, @upd_user, @upd_user_no, @upd_user_name, @upd_host);
