sqls.sql:

SELECT 
	dept_id, dept_wkp_rmk1, dept_wkp_rmk2, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host
FROM ss_dept_wkp;


INSERT INTO ss_dept_wkp 
(dept_id, dept_wkp_rmk1, dept_wkp_rmk2, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host)
VALUES (@dept_id, @dept_wkp_rmk1, @dept_wkp_rmk2, @crt_time, @crt_user, @crt_user_no, @crt_user_name, @crt_host, @upd_time, @upd_user, @upd_user_no, @upd_user_name, @upd_host);


UPDATE ss_dept_wkp SET 
dept_wkp_rmk1 = @dept_wkp_rmk1, dept_wkp_rmk2 = @dept_wkp_rmk2, crt_time = @crt_time, crt_user = @crt_user, crt_user_no = @crt_user_no, crt_user_name = @crt_user_name, crt_host = @crt_host, upd_time = @upd_time, upd_user = @upd_user, upd_user_no = @upd_user_no, upd_user_name = @upd_user_name, upd_host = @upd_host
WHERE dept_id = @dept_id OR dept_wkp_id = @dept_wkp_id;
