sqls.sql:

SELECT 
	grid_bb_id, cell_sql_where, cell_color_background, cell_font_color, cell_style_to_row, ss_grid_bc_rmk01, ss_grid_bc_rmk02, crt_user, crt_user_no, crt_user_name, crt_host, crt_time, upd_user, upd_user_no, upd_user_name, upd_host, upd_time
FROM ss_grid_bc;


INSERT INTO ss_grid_bc 
(grid_bb_id, cell_sql_where, cell_color_background, cell_font_color, cell_style_to_row, ss_grid_bc_rmk01, ss_grid_bc_rmk02, crt_user, crt_user_no, crt_user_name, crt_host, crt_time, upd_user, upd_user_no, upd_user_name, upd_host, upd_time)
VALUES (@grid_bb_id, @cell_sql_where, @cell_color_background, @cell_font_color, @cell_style_to_row, @ss_grid_bc_rmk01, @ss_grid_bc_rmk02, @crt_user, @crt_user_no, @crt_user_name, @crt_host, @crt_time, @upd_user, @upd_user_no, @upd_user_name, @upd_host, @upd_time);


UPDATE ss_grid_bc SET 
grid_bb_id = @grid_bb_id, cell_sql_where = @cell_sql_where, cell_color_background = @cell_color_background, cell_font_color = @cell_font_color, cell_style_to_row = @cell_style_to_row, ss_grid_bc_rmk01 = @ss_grid_bc_rmk01, ss_grid_bc_rmk02 = @ss_grid_bc_rmk02, crt_user = @crt_user, crt_user_no = @crt_user_no, crt_user_name = @crt_user_name, crt_host = @crt_host, crt_time = @crt_time, upd_user = @upd_user, upd_user_no = @upd_user_no, upd_user_name = @upd_user_name, upd_host = @upd_host, upd_time = @upd_time
WHERE grid_bc_id = @grid_bc_id;
