sqls.sql:

SELECT 
	ztable, pk_field, pk_data, data_field, data_before, data_after, prog_id, crt_time, crt_user, crt_user_no, crt_user_name, crt_host
FROM ss_log_update;


INSERT INTO ss_log_update 
(ztable, pk_field, pk_data, data_field, data_before, data_after, prog_id, crt_time, crt_user, crt_user_no, crt_user_name, crt_host)
VALUES (@ztable, @pk_field, @pk_data, @data_field, @data_before, @data_after, @prog_id, @crt_time, @crt_user, @crt_user_no, @crt_user_name, @crt_host);


UPDATE ss_log_update SET 
ztable = @ztable, pk_field = @pk_field, pk_data = @pk_data, data_field = @data_field, data_before = @data_before, data_after = @data_after, prog_id = @prog_id, crt_time = @crt_time, crt_user = @crt_user, crt_user_no = @crt_user_no, crt_user_name = @crt_user_name, crt_host = @crt_host
WHERE tuid = @tuid;
