SELECT 
	msg_rule_h_id, msg_receiver_worker_id, crt_time, crt_user, crt_user_no, crt_user_name, crt_host
FROM ss_msg_rule_r;


INSERT INTO ss_msg_rule_r 
(msg_rule_h_id, msg_receiver_worker_id, crt_time, crt_user, crt_user_no, crt_user_name, crt_host)
VALUES (@msg_rule_h_id, @msg_receiver_worker_id, @crt_time, @crt_user, @crt_user_no, @crt_user_name, @crt_host);


UPDATE ss_msg_rule_r SET 
msg_rule_h_id = @msg_rule_h_id, msg_receiver_worker_id = @msg_receiver_worker_id, crt_time = @crt_time, crt_user = @crt_user, crt_user_no = @crt_user_no, crt_user_name = @crt_user_name, crt_host = @crt_host
WHERE msg_rule_r_id = @msg_rule_r_id;
