sqls.sql:

SELECT 
	no_rule_h_no, no_rule_h_name, no_rule_h_rmk01, no_rule_h_rmk02, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host
FROM ss_no_rule_h;


INSERT INTO ss_no_rule_h 
(no_rule_h_no, no_rule_h_name, no_rule_h_rmk01, no_rule_h_rmk02, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host)
VALUES (@no_rule_h_no, @no_rule_h_name, @no_rule_h_rmk01, @no_rule_h_rmk02, @crt_time, @crt_user, @crt_user_no, @crt_user_name, @crt_host, @upd_time, @upd_user, @upd_user_no, @upd_user_name, @upd_host);


UPDATE ss_no_rule_h SET 
no_rule_h_no = @no_rule_h_no, no_rule_h_name = @no_rule_h_name, no_rule_h_rmk01 = @no_rule_h_rmk01, no_rule_h_rmk02 = @no_rule_h_rmk02, crt_time = @crt_time, crt_user = @crt_user, crt_user_no = @crt_user_no, crt_user_name = @crt_user_name, crt_host = @crt_host, upd_time = @upd_time, upd_user = @upd_user, upd_user_no = @upd_user_no, upd_user_name = @upd_user_name, upd_host = @upd_host
WHERE no_rule_h_id = @no_rule_h_id;
