sqls.sql:

SELECT 
	toolbar_name, toolbar_icon, toolbar_type, toolbar_lang_str, toolbar_tip_lang_str, toolbar_rmk01, toolbar_rmk02, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host
FROM ss_toolbar2;


INSERT INTO ss_toolbar2 
(toolbar_name, toolbar_icon, toolbar_type, toolbar_lang_str, toolbar_tip_lang_str, toolbar_rmk01, toolbar_rmk02, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host)
VALUES (@toolbar_name, @toolbar_icon, @toolbar_type, @toolbar_lang_str, @toolbar_tip_lang_str, @toolbar_rmk01, @toolbar_rmk02, @crt_time, @crt_user, @crt_user_no, @crt_user_name, @crt_host, @upd_time, @upd_user, @upd_user_no, @upd_user_name, @upd_host);


UPDATE ss_toolbar2 SET 
toolbar_name = @toolbar_name, toolbar_icon = @toolbar_icon, toolbar_type = @toolbar_type, toolbar_lang_str = @toolbar_lang_str, toolbar_tip_lang_str = @toolbar_tip_lang_str, toolbar_rmk01 = @toolbar_rmk01, toolbar_rmk02 = @toolbar_rmk02, crt_time = @crt_time, crt_user = @crt_user, crt_user_no = @crt_user_no, crt_user_name = @crt_user_name, crt_host = @crt_host, upd_time = @upd_time, upd_user = @upd_user, upd_user_no = @upd_user_no, upd_user_name = @upd_user_name, upd_host = @upd_host
WHERE toolbar_id = @toolbar_id;
