sqls.sql:

SELECT 
	user_superior, rmk1, rmk2, rmk3, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host
FROM ss_user_superior;


INSERT INTO ss_user_superior 
(user_superior, rmk1, rmk2, rmk3, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host)
VALUES (@user_superior, @rmk1, @rmk2, @rmk3, @crt_time, @crt_user, @crt_user_no, @crt_user_name, @crt_host, @upd_time, @upd_user, @upd_user_no, @upd_user_name, @upd_host);


UPDATE ss_user_superior SET 
user_superior = @user_superior, rmk1 = @rmk1, rmk2 = @rmk2, rmk3 = @rmk3, crt_time = @crt_time, crt_user = @crt_user, crt_user_no = @crt_user_no, crt_user_name = @crt_user_name, crt_host = @crt_host, upd_time = @upd_time, upd_user = @upd_user, upd_user_no = @upd_user_no, upd_user_name = @upd_user_name, upd_host = @upd_host
WHERE user_id = @user_id;
