sqls.sql:

SELECT 
	ver_id, ver_release, ver_date, ver_type, ver_desc, ver_file, ver_h_pda_rmk1, ver_h_pda_rmk2, crt_time, crt_user, crt_user_no, crt_user_name, upd_time, crt_host, upd_user, upd_user_no, upd_user_name, upd_host
FROM ss_ver_h_pda;


INSERT INTO ss_ver_h_pda 
(ver_id, ver_release, ver_date, ver_type, ver_desc, ver_file, ver_h_pda_rmk1, ver_h_pda_rmk2, crt_time, crt_user, crt_user_no, crt_user_name, upd_time, crt_host, upd_user, upd_user_no, upd_user_name, upd_host)
VALUES (@ver_id, @ver_release, @ver_date, @ver_type, @ver_desc, @ver_file, @ver_h_pda_rmk1, @ver_h_pda_rmk2, @crt_time, @crt_user, @crt_user_no, @crt_user_name, @upd_time, @crt_host, @upd_user, @upd_user_no, @upd_user_name, @upd_host);


UPDATE ss_ver_h_pda SET 
ver_id = @ver_id, ver_release = @ver_release, ver_date = @ver_date, ver_type = @ver_type, ver_desc = @ver_desc, ver_file = @ver_file, ver_h_pda_rmk1 = @ver_h_pda_rmk1, ver_h_pda_rmk2 = @ver_h_pda_rmk2, crt_time = @crt_time, crt_user = @crt_user, crt_user_no = @crt_user_no, crt_user_name = @crt_user_name, upd_time = @upd_time, crt_host = @crt_host, upd_user = @upd_user, upd_user_no = @upd_user_no, upd_user_name = @upd_user_name, upd_host = @upd_host
WHERE ver_h_id = @ver_h_id;
