SELECT 
	wm_io_h_id, sn_no, part_no, part_name, part_spec, part_unit, part_unit_name, part_idt, mrp_region_no, lot_no, invp_no, invp_name, part_qty, wm_io_sn_part_rmk1, wm_io_sn_part_rmk2, wm_io_sn_part_rmk3, wm_io_sn_part_rmk4, wm_io_sn_part_rmk5, wm_io_sn_part_rmk6, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host, io_is_sucessed, io_times, io_last_time
FROM wm_io_sn_part;


INSERT INTO wm_io_sn_part 
(wm_io_h_id, sn_no, part_no, part_name, part_spec, part_unit, part_unit_name, part_idt, mrp_region_no, lot_no, invp_no, invp_name, part_qty, wm_io_sn_part_rmk1, wm_io_sn_part_rmk2, wm_io_sn_part_rmk3, wm_io_sn_part_rmk4, wm_io_sn_part_rmk5, wm_io_sn_part_rmk6, crt_time, crt_user, crt_user_no, crt_user_name, crt_host, upd_time, upd_user, upd_user_no, upd_user_name, upd_host, io_is_sucessed, io_times, io_last_time)
VALUES (@wm_io_h_id, @sn_no, @part_no, @part_name, @part_spec, @part_unit, @part_unit_name, @part_idt, @mrp_region_no, @lot_no, @invp_no, @invp_name, @part_qty, @wm_io_sn_part_rmk1, @wm_io_sn_part_rmk2, @wm_io_sn_part_rmk3, @wm_io_sn_part_rmk4, @wm_io_sn_part_rmk5, @wm_io_sn_part_rmk6, @crt_time, @crt_user, @crt_user_no, @crt_user_name, @crt_host, @upd_time, @upd_user, @upd_user_no, @upd_user_name, @upd_host, @io_is_sucessed, @io_times, @io_last_time);


UPDATE wm_io_sn_part SET 
wm_io_h_id = @wm_io_h_id, sn_no = @sn_no, part_no = @part_no, part_name = @part_name, part_spec = @part_spec, part_unit = @part_unit, part_unit_name = @part_unit_name, part_idt = @part_idt, mrp_region_no = @mrp_region_no, lot_no = @lot_no, invp_no = @invp_no, invp_name = @invp_name, part_qty = @part_qty, wm_io_sn_part_rmk1 = @wm_io_sn_part_rmk1, wm_io_sn_part_rmk2 = @wm_io_sn_part_rmk2, wm_io_sn_part_rmk3 = @wm_io_sn_part_rmk3, wm_io_sn_part_rmk4 = @wm_io_sn_part_rmk4, wm_io_sn_part_rmk5 = @wm_io_sn_part_rmk5, wm_io_sn_part_rmk6 = @wm_io_sn_part_rmk6, crt_time = @crt_time, crt_user = @crt_user, crt_user_no = @crt_user_no, crt_user_name = @crt_user_name, crt_host = @crt_host, upd_time = @upd_time, upd_user = @upd_user, upd_user_no = @upd_user_no, upd_user_name = @upd_user_name, upd_host = @upd_host, io_is_sucessed = @io_is_sucessed, io_times = @io_times, io_last_time = @io_last_time
WHERE wm_io_sn_part_id = @wm_io_sn_part_id;
